// ═══════════════════════════════════════════════════════════════════════
// UNIFIED BEDTIME CONTROLLER
// Handles all bedtime mode logic in one place
// Output 1: Continue to normal temp control (bedtime inactive OR bedtime complete)
// Output 2: Bedtime cooling active - send direct to heat pump
// ═══════════════════════════════════════════════════════════════════════

const bedTimeMode = flow.get('bedTimeMode') || false;
const downstairsTemp = Number(flow.get('downstairsCurrentTemp')) || 20;
const currentHour = new Date().getHours();
const bedtimeTries = flow.get('bedtimeTries') || 0;

// Bedtime configuration
const BEDTIME_TARGET_TEMP = 19;        // Goal: downstairs at 19°C for sleeping
const BEDTIME_COOLING_TEMP = 16;       // Aggressive cooling setpoint
const BEDTIME_FAN = 'medium';          // Not too loud for sleeping
const BEDTIME_SWING = 'fixedBottom';   // Always blow down for bedroom comfort
const MAX_BEDTIME_CYCLES = 6;          // Safety: max 60 min of bedtime cooling

// Weekend detection
const isWeekend = global.get('isWeekend') || false;
const isWeekendMorning = isWeekend && currentHour >= 6 && currentHour < 10;

// ─────────────────────────────────────────────────────────────────────
// NOT IN BEDTIME MODE - pass through to normal control
// ─────────────────────────────────────────────────────────────────────
if (!bedTimeMode) {
    // Add weekend quiet hours info for downstream processing
    if (isWeekendMorning) {
        msg.quietHours = { active: true, maxFan: 'medium', reason: 'Weekend quiet hours' };
    }
    node.status({fill: 'green', shape: 'ring', text: 'Normal mode'});
    return [msg, null];
}

// ─────────────────────────────────────────────────────────────────────
// BEDTIME MODE ACTIVE
// ─────────────────────────────────────────────────────────────────────

// Safety check: too many cycles?
if (bedtimeTries >= MAX_BEDTIME_CYCLES) {
    flow.set('bedTimeMode', false);
    flow.set('tempOn', true);
    node.status({fill: 'yellow', shape: 'ring', text: 'Bedtime timeout - resuming auto'});
    node.warn('Bedtime mode timed out after ' + MAX_BEDTIME_CYCLES + ' cycles');
    return [msg, null];
}

// Check if we've reached bedtime goal
if (downstairsTemp <= BEDTIME_TARGET_TEMP) {
    // Goal reached! Reset bedtime mode
    flow.set('bedTimeMode', false);
    flow.set('tempOn', false);

    node.status({fill: 'green', shape: 'dot', text: 'Bedtime complete: ' + downstairsTemp.toFixed(1) + '°C'});

    // Send notification that bedtime is complete
    const completeMsg = {
        payload: 'Bedtime cooling complete. Downstairs: ' + downstairsTemp.toFixed(1) + '°C',
        bedtimeComplete: true
    };

    return [null, null, completeMsg]; // Resume normal + notify
}

// ─────────────────────────────────────────────────────────────────────
// STILL COOLING - enforce bedtime settings
// ─────────────────────────────────────────────────────────────────────
flow.set('bedtimeTries', bedtimeTries + 1);

// Set bedtime cooling parameters
flow.set('mode', 'cool');
flow.set('swing', BEDTIME_SWING);
flow.set('correctedTemp', BEDTIME_COOLING_TEMP);
flow.set('fanlevel', BEDTIME_FAN);
flow.set('shouldTurnOn', true);
flow.set('controlReason', 'Bedtime cooling: ' + downstairsTemp.toFixed(1) + '°C → ' + BEDTIME_TARGET_TEMP + '°C');

const bedtimeMsg = {
    payload: {
        mode: 'cool',
        fanLevel: BEDTIME_FAN,
        heatPumpTemp: BEDTIME_COOLING_TEMP,
        swing: BEDTIME_SWING,
        shouldTurnOn: true,
        reason: 'Bedtime cooling cycle ' + (bedtimeTries + 1) + '/' + MAX_BEDTIME_CYCLES,
        downstairsTemp: downstairsTemp,
        targetTemp: BEDTIME_TARGET_TEMP
    },
    on: true
};

node.status({fill: 'blue', shape: 'dot', text: 'Bedtime: ' + downstairsTemp.toFixed(1) + '°C → ' + BEDTIME_TARGET_TEMP + '°C [' + (bedtimeTries + 1) + '/' + MAX_BEDTIME_CYCLES + ']'});

return [null, bedtimeMsg]; // Output 2: direct to heat pump

