// ─────────────────────────────────────────────────────────────────────
const isSummer = flow.get('SummerTime');
const seasonName = flow.get('seasonName');
const focusArea = flow.get('focusArea') || 'downstairs';
const primaryMode = flow.get('primaryMode');
const defaultSwing = flow.get('defaultSwing') || 'fixedBottom';
const safetyMin = flow.get('safetyMin') || 15;
const safetyMax = flow.get('safetyMax') || 30;

// ─────────────────────────────────────────────────────────────────────
// SENSOR INPUTS & CURRENT STATE
// ─────────────────────────────────────────────────────────────────────
const desiredTemp = Number(flow.get('desiredTemp')) || Number(msg.desiredTemp) || 21;
const upstairsCurrentTemp = Number(flow.get('upstairsCurrentTemp')) || Number(msg.upstairsCurrentTemp) || 20;
const downstairsCurrentTemp = Number(flow.get('downstairsCurrentTemp')) || Number(msg.downstairsCurrentTemp) || 20;
const outsideTemp = Number(flow.get('OutsideTemp')) || Number(msg.OutsideTemp) || 20;
const lastModeChange = flow.get('lastModeChange') || 0;
const isMorning = msg.isMorning === true;

// ─────────────────────────────────────────────────────────────────────
// OCCUPANCY, TIME & MODE CONTEXT
// ─────────────────────────────────────────────────────────────────────
const homeStatus = global.get('Mode') || 'home';
const dayStatus = flow.get('status') || 'day';
const isHome = homeStatus === 'home';
const isDay = dayStatus === 'day';
const currentHour = new Date().getHours();

// **NEW: Bedtime and Weekend awareness**
const bedTimeMode = flow.get('bedTimeMode') || false;
const isWeekend = global.get('isWeekend') || false;
const isWeekendQuietHours = isWeekend && currentHour < WEEKEND_QUIET_UNTIL_HOUR;

// ─────────────────────────────────────────────────────────────────────
// STATE TRACKING & PERFORMANCE HISTORY
// ─────────────────────────────────────────────────────────────────────
const tempHistory = Array.isArray(flow.get('tempHistory')) ? flow.get('tempHistory') : [];
const lastTargetTemp = flow.get('lastTargetTemp');
const lastFanLevel = flow.get('lastFanLevel');
const performanceHistory = flow.get('performanceHistory') || {};

const now = Date.now();
const secondsSinceLastChange = (now - lastModeChange) / 1000;

// ─────────────────────────────────────────────────────────────────────
// CONTROL OUTPUT DEFAULTS
// ─────────────────────────────────────────────────────────────────────
let controlMode = 'fan';
let targetTemp = desiredTemp;
let swing = defaultSwing;
let fanLevel = 'low';
let reason = '';
let shouldTurnOn = false;

// ─────────────────────────────────────────────────────────────────────
// HELPERS
// ─────────────────────────────────────────────────────────────────────
function weightedAverage(up, down, upWeight = isSummer ? 0.75 : 0.65) {
    return up * upWeight + down * (1 - upWeight);
}
function clamp(v, lo, hi) {
    return Math.max(lo, Math.min(hi, v));
}
function getPriorityZone() {
    // During bedtime, always prioritize downstairs (where bedroom is)
    if (bedTimeMode) return 'downstairs';
    if (focusArea === 'upstairs' || focusArea === 'downstairs') return focusArea;
    if (isHome && isDay && currentHour >= 9 && currentHour <= 21) return 'upstairs';
    return 'downstairs';
}
function getReferenceTemp(priorityZone) {
    if (priorityZone === 'upstairs') return upstairsCurrentTemp * 0.6 + downstairsCurrentTemp * 0.4;
    return downstairsCurrentTemp * 0.75 + upstairsCurrentTemp * 0.25;
}
function needsZoneBalancing() {
    const zoneDiff = Math.abs(upstairsCurrentTemp - downstairsCurrentTemp);
    return zoneDiff > CIRCULATION_TEMP_DIFF;
}

// **NEW: Fan level limiter for quiet hours**
function limitFanForQuietHours(requestedFan) {
    if (!isWeekendQuietHours && !bedTimeMode) return requestedFan;

    const fanLevels = ['quiet', 'low', 'medium', 'medium_high', 'high', 'strong', 'auto'];
    const maxAllowed = bedTimeMode ? 'medium' : WEEKEND_MAX_FAN_QUIET;
    const maxIndex = fanLevels.indexOf(maxAllowed);
    const requestedIndex = fanLevels.indexOf(requestedFan);

    if (requestedIndex > maxIndex) {
        return maxAllowed;
    }
    return requestedFan;
}

// **NEW: Outside temperature reactivity factor**
function getOutsideTempFactor() {
    // Returns a multiplier for heating/cooling aggression based on outside temp
    // Very cold outside = more aggressive heating
    // Very hot outside = more aggressive cooling
    if (outsideTemp <= -5) return 1.5;   // Very cold
    if (outsideTemp <= 0) return 1.3;    // Cold
    if (outsideTemp <= 5) return 1.15;   // Cool
    if (outsideTemp >= 30) return 1.4;   // Very hot (cooling)
    if (outsideTemp >= 25) return 1.2;   // Hot
    return 1.0;                           // Mild
}

function updatePerformance() {
    if (!lastTargetTemp || !lastFanLevel || tempHistory.length < 1) return;
    const recordedStartTemp = tempHistory[0];
    const minutes = (Date.now() - lastModeChange) / 1000 / 60;
    if (minutes < 5) return;

    const tempChange = upstairsCurrentTemp - recordedStartTemp;
    const perf = {
        targetTemp: lastTargetTemp,
        fanLevel: lastFanLevel,
        tempChange,
        outsideTemp,
        durationMin: minutes,
        timestamp: new Date().toISOString()
    };

    performanceHistory[lastFanLevel] = performanceHistory[lastFanLevel] || [];
    performanceHistory[lastFanLevel].push(perf);
    if (performanceHistory[lastFanLevel].length > 10) performanceHistory[lastFanLevel].shift();
    flow.set('performanceHistory', performanceHistory);
}

// ─────────────────────────────────────────────────────────────────────
// ADAPTIVE MORNING: compute effective desired temp
// ─────────────────────────────────────────────────────────────────────
let effectiveDesired = desiredTemp;
const isMildWinterMorning = !isSummer && isMorning;

if (isMildWinterMorning) {
    let mt = MORNING_BASE + MORNING_SLOPE * Math.max(0, 5 - outsideTemp);

    if (upstairsCurrentTemp >= (UPSTAIRS_COMFORT_MIN + 0.2)) {
        mt = Math.min(mt, MORNING_BASE);
    }

    const zoneDiff = Math.abs(upstairsCurrentTemp - downstairsCurrentTemp);
    if (zoneDiff > MAX_ZONE_DIFFERENCE) {
        mt = Math.min(mt, MORNING_BASE + 0.3);
    }

    if (outsideTemp >= 8 && downstairsCurrentTemp >= 21.0) {
        mt = Math.min(mt, MORNING_BASE);
    }

    effectiveDesired = clamp(mt, safetyMin, Math.min(safetyMax, MORNING_MAX));
}

// ─────────────────────────────────────────────────────────────────────
// CONTROL APPLICATION FUNCTIONS
// ─────────────────────────────────────────────────────────────────────
function applyCirculationLogic(context) {
    controlMode = 'fan';
    swing = 'rangeFull';
    fanLevel = limitFanForQuietHours('low');
    shouldTurnOn = true;
    reason = context;
}

function applyHeatingLogic(context, referenceTemp, priorityZone = 'downstairs', targetSetpoint = desiredTemp) {
    controlMode = 'heat';
    shouldTurnOn = true;

    // **FIXED: Bedtime mode always uses fixedBottom for downstairs comfort**
    if (bedTimeMode) {
        swing = 'fixedBottom';
    } else {
        const isUpstairsFocus = priorityZone === 'upstairs';
        swing = isUpstairsFocus ? 'fixedTop' : 'fixedBottom';
    }

    const isUpstairsFocus = priorityZone === 'upstairs' && !bedTimeMode;
    const tempDiff = targetSetpoint - referenceTemp;

    // **IMPROVED: Outside temp reactivity affects aggression**
    let aggression = getOutsideTempFactor();

    // Less aggressive on mild winter mornings
    if (!isSummer && isMorning && outsideTemp >= 5) aggression *= 0.5;
    if (!isSummer && isMorning && outsideTemp >= 8) aggression *= 0.7; // 0.35 total

    let requestedFan = 'low';
    if (tempDiff > 2.5) {
        requestedFan = isUpstairsFocus ? 'medium' : (isMorning ? 'medium' : 'high');
        targetTemp = clamp(targetSetpoint + 1.5 * aggression, -100, safetyMax);
    } else if (tempDiff > 1.5) {
        requestedFan = isUpstairsFocus ? 'low' : (isMorning ? 'medium' : 'medium_high');
        targetTemp = clamp(targetSetpoint + 1.0 * aggression, -100, safetyMax);
    } else if (tempDiff > 0.7) {
        requestedFan = isUpstairsFocus ? 'low' : (isMorning ? 'low' : 'medium');
        targetTemp = clamp(targetSetpoint + 0.6 * aggression, -100, safetyMax);
    } else {
        requestedFan = 'low';
        targetTemp = targetSetpoint;
    }

    // Gentle approach near target in mornings
    if (isMorning && tempDiff <= GENTLE_NEAR_THRESHOLD) {
        requestedFan = 'low';
    }

    // **NEW: Apply quiet hours fan limiting**
    fanLevel = limitFanForQuietHours(requestedFan);

    // Performance-based adjustment
    const perfList = performanceHistory[lastFanLevel || fanLevel] || [];
    if (perfList.length >= 3) {
        const avgRate = perfList.reduce((s, p) => s + (p.tempChange / Math.max(0.1, p.durationMin)), 0) / perfList.length;
        if (avgRate < 0.08) {
            targetTemp = clamp(targetTemp + LEARNING_RATE * 2, -100, safetyMax);
        }
    }

    // Add context about quiet mode if active
    let contextSuffix = '';
    if (bedTimeMode) contextSuffix += ' [Bedtime: fixedBottom]';
    if (isWeekendQuietHours) contextSuffix += ' [Weekend quiet hours]';

    reason = context + contextSuffix;
    flow.set('lastTargetTemp', targetTemp);
    flow.set('lastFanLevel', fanLevel);
}

function applyCoolingLogic(context) {
    controlMode = 'cool';
    swing = 'fixedTop';
    shouldTurnOn = true;

    const tempDiff = upstairsCurrentTemp - desiredTemp;
    if (tempDiff <= COMFORT_TOLERANCE) {
        applyCirculationLogic(`Upstairs slightly above target (${upstairsCurrentTemp}°C), prefer circulation`);
        return;
    }

    // **IMPROVED: Outside temp reactivity for cooling**
    const aggression = getOutsideTempFactor();

    let requestedFan = 'low';
    if (tempDiff > 3.5) {
        requestedFan = 'high';
        targetTemp = clamp(desiredTemp - 2.0 * aggression, safetyMin, safetyMax);
    } else if (tempDiff > 2.2) {
        requestedFan = 'medium_high';
        targetTemp = clamp(desiredTemp - 1.2 * aggression, safetyMin, safetyMax);
    } else if (tempDiff > 1.0) {
        requestedFan = 'medium';
        targetTemp = clamp(desiredTemp - 0.8 * aggression, safetyMin, safetyMax);
    } else {
        requestedFan = 'low';
        targetTemp = desiredTemp;
    }

    // **NEW: Apply quiet hours fan limiting for cooling too**
    fanLevel = limitFanForQuietHours(requestedFan);

    let contextSuffix = '';
    if (isWeekendQuietHours) contextSuffix += ' [Weekend quiet hours]';

    reason = context + contextSuffix;
    flow.set('lastTargetTemp', targetTemp);
    flow.set('lastFanLevel', fanLevel);
}

// ═══════════════════════════════════════════════════════════════════════
/** MAIN DECISION LOGIC */
// ═══════════════════════════════════════════════════════════════════════
const priorityZone = getPriorityZone();
const referenceTemp = getReferenceTemp(priorityZone);
const zoneDiff = Math.abs(upstairsCurrentTemp - downstairsCurrentTemp);
const weighted = weightedAverage(upstairsCurrentTemp, downstairsCurrentTemp);

// ─────────────────────────────────────────────────────────────────────
// **NEW: BEDTIME MODE OVERRIDE - Skip normal logic if bedtime cooling active**
// The Bedtime flow already handles cooling, this function should respect that
// ─────────────────────────────────────────────────────────────────────
// Note: If bedTimeMode is true and we're in heating scenario, ensure downstairs focus

// ─────────────────────────────────────────────────────────────────────
// MORNING HEATING CYCLE (All Seasons, adaptive in mild winters)
// ─────────────────────────────────────────────────────────────────────
if (isMorning) {
    if (isSummer && downstairsCurrentTemp < SUMMER_MORNING_HEAT_THRESHOLD) {
        applyHeatingLogic('Summer morning gentle heat', downstairsCurrentTemp, 'downstairs', desiredTemp);
    } else {
        const contextText = !isSummer
            ? `Adaptive Morning (downstairs focus), out ${outsideTemp}°C, up ${upstairsCurrentTemp.toFixed(1)}°C`
            : `Morning heating cycle (${priorityZone} focus)`;
        const ref = !isSummer ? downstairsCurrentTemp : referenceTemp;
        const prio = !isSummer ? 'downstairs' : priorityZone;
        applyHeatingLogic(contextText, ref, prio, effectiveDesired);
    }
}
// ─────────────────────────────────────────────────────────────────────
// SUMMER COOLING & CIRCULATION
// ─────────────────────────────────────────────────────────────────────
else if (isSummer) {
    if (upstairsCurrentTemp >= AIR_CIRCULATION_THRESHOLD) {
        if (upstairsCurrentTemp >= desiredTemp + 1.0) {
            applyCoolingLogic(`Cooling needed: Upstairs ${upstairsCurrentTemp}°C above target`);
        } else {
            applyCirculationLogic(`Air circulation: Managing warm upstairs temp`);
        }
    } else if (weighted < desiredTemp - COMFORT_TOLERANCE) {
        applyHeatingLogic(`Comfort heating: rooms feel cold`, weighted, priorityZone, desiredTemp);
    } else if (upstairsCurrentTemp > safetyMax) {
        applyCoolingLogic(`EMERGENCY: Upstairs temperature too high`);
    } else if (upstairsCurrentTemp >= desiredTemp + 0.8) {
        applyCoolingLogic(`Active cooling: Managing upstairs temp`);
    } else {
        controlMode = 'fan';
        shouldTurnOn = false;
        reason = `Temperature within comfort range`;
    }
}
// ─────────────────────────────────────────────────────────────────────
// WINTER HEATING & ZONE BALANCING
// ─────────────────────────────────────────────────────────────────────
else {
    // **MODIFIED: During bedtime, skip upstairs comfort heating - focus on downstairs**
    if (!bedTimeMode && upstairsCurrentTemp < UPSTAIRS_COMFORT_MIN && isHome && isDay &&
        downstairsCurrentTemp - upstairsCurrentTemp < 3.0) {
        applyHeatingLogic(
            `Winter upstairs comfort heating (${upstairsCurrentTemp}°C < ${UPSTAIRS_COMFORT_MIN}°C)`,
            upstairsCurrentTemp,
            'upstairs',
            desiredTemp
        );
    }
    else if (downstairsCurrentTemp < desiredTemp - COMFORT_TOLERANCE) {
        applyHeatingLogic(
            `Primary heating: Downstairs below target (${downstairsCurrentTemp}°C)`,
            downstairsCurrentTemp,
            'downstairs',
            desiredTemp
        );
    }
    else if (needsZoneBalancing() && controlMode !== 'off') {
        if (downstairsCurrentTemp < upstairsCurrentTemp - MAX_ZONE_DIFFERENCE) {
            applyHeatingLogic(
                `Zone balancing: Downstairs ${zoneDiff.toFixed(1)}°C colder`,
                downstairsCurrentTemp,
                'downstairs',
                desiredTemp
            );
        } else if (!bedTimeMode && upstairsCurrentTemp < downstairsCurrentTemp - CIRCULATION_TEMP_DIFF) {
            applyHeatingLogic(
                `Zone balancing: Upstairs ${zoneDiff.toFixed(1)}°C colder`,
                upstairsCurrentTemp,
                'upstairs',
                desiredTemp
            );
        } else {
            applyCirculationLogic(`Zone circulation: ${zoneDiff.toFixed(1)}°C difference`);
        }
    }
    else if (referenceTemp < desiredTemp - COMFORT_TOLERANCE) {
        applyHeatingLogic(
            `Heating: ${priorityZone} focus (${referenceTemp.toFixed(1)}°C < ${desiredTemp}°C)`,
            referenceTemp,
            priorityZone,
            desiredTemp
        );
    }
    else {
        controlMode = 'fan';
        shouldTurnOn = false;
        reason = `Within comfort range (${priorityZone} ${referenceTemp.toFixed(1)}°C, zones balanced)`;
    }
}

// ─────────────────────────────────────────────────────────────────────
// SAFETY CHECKS & STATE MANAGEMENT
// ─────────────────────────────────────────────────────────────────────
if (flow.get('lastControlMode') !== controlMode) {
    if (secondsSinceLastChange < MIN_ON_OFF_TIME_SEC) {
        shouldTurnOn = false;
        controlMode = 'fan';
        reason += ` [Min ON/OFF time not elapsed]`;
    } else {
        flow.set('lastModeChange', now);
        flow.set('lastControlMode', controlMode);
    }
}

// Update temp history
tempHistory.unshift(upstairsCurrentTemp);
if (tempHistory.length > MAX_TEMP_HISTORY) tempHistory.pop();
flow.set('tempHistory', tempHistory);

// Update performance learning
updatePerformance();

// Store debug info
flow.set('currentPriorityZone', getPriorityZone());
flow.set('zoneTempDifference', zoneDiff);

// ─────────────────────────────────────────────────────────────────────
// FINAL OUTPUT & STATUS
// ─────────────────────────────────────────────────────────────────────
flow.set('mode', controlMode);
flow.set('shouldTurnOn', shouldTurnOn);
flow.set('controlReason', reason);
flow.set('swing', swing);
flow.set('fanlevel', fanLevel);
flow.set('correctedTemp', Math.round(targetTemp));

return {
    payload: {
        mode: controlMode,
        fanLevel: fanLevel,
        heatPumpTemp: Math.round(targetTemp),
        swing: swing,
        shouldTurnOn: shouldTurnOn,
        season: seasonName,
        priorityZone: getPriorityZone(),
        upstairsTemp: upstairsCurrentTemp,
        downstairsTemp: downstairsCurrentTemp,
        zoneDifference: zoneDiff.toFixed(1),
        outsideTemp: outsideTemp,
        desiredTemp: desiredTemp,
        referenceTemp: getReferenceTemp(getPriorityZone()).toFixed(1),
        reason: reason,
        bedTimeMode: bedTimeMode,
        isWeekendQuietHours: isWeekendQuietHours,
        timestamp: new Date().toISOString(),
        performanceMetrics: performanceHistory[fanLevel] || []
    }
};

